mapper.select:
select wms_cre_housing_att_id, data_type, attachment_old_name, attachment_new_name, attachment_address, attachment_type, wms_cre_credit_head_id, create_user_id, create_user_name, date_format(create_timestamp,'%Y-%m-%d %H:%i:%s') as create_timestamp_str, create_timestamp, last_update_user_id, date_format(last_update_timestamp,'%Y-%m-%d %H:%i:%s') as last_update_timestamp_str, last_update_timestamp, enable_flag from wms_cre_credit_att
enable_flag = '1'
and wms_cre_credit_head_id = #{wms_cre_credit_head_id}
and data_type = #{data_type}

select
wms_cre_housing_att_id,
data_type,
attachment_old_name,
attachment_new_name,
attachment_address,
attachment_type,
wms_cre_credit_head_id,
create_user_id,
create_user_name,
date_format(create_timestamp,'%Y-%m-%d %H:%i:%s') as create_timestamp_str,
create_timestamp,
last_update_user_id,
date_format(last_update_timestamp,'%Y-%m-%d %H:%i:%s') as last_update_timestamp_str,
last_update_timestamp,
enable_flag
from
wms_cre_credit_att


		and wms_cre_housing_att_id = #{wms_cre_housing_att_id}


		and data_type = #{data_type}


		and attachment_old_name = #{attachment_old_name}


		and attachment_new_name = #{attachment_new_name}


		and attachment_address = #{attachment_address}


		and attachment_type = #{attachment_type}


		and wms_cre_credit_head_id = #{wms_cre_credit_head_id}


		and create_user_id = #{create_user_id}


		and create_user_name = #{create_user_name}


		and create_timestamp = #{create_timestamp}


		and last_update_user_id = #{last_update_user_id}


		and last_update_timestamp = #{last_update_timestamp}


		and enable_flag = #{enable_flag}


		and wms_cre_housing_att_id != #{wms_cre_housing_att_id}
