mapper.select:
select wms_cre_credit_line_other, business_model, remark, wms_cre_credit_head_id, create_user_id, create_user_name, date_format(create_timestamp,'%Y-%m-%d %H:%i:%s') as create_timestamp_str, create_timestamp, last_update_user_id, date_format(last_update_timestamp,'%Y-%m-%d %H:%i:%s') as last_update_timestamp_str, last_update_timestamp, enable_flag wms_cre_credit_line_other
ORDER BY ${sortname} ${sortorder}
LIMIT ${offset} , ${pagesize}

select
wms_cre_credit_line_other,
business_model,
remark,
wms_cre_credit_head_id,
create_user_id,
create_user_name,
date_format(create_timestamp,'%Y-%m-%d %H:%i:%s') as create_timestamp_str,
create_timestamp,
last_update_user_id,
date_format(last_update_timestamp,'%Y-%m-%d %H:%i:%s') as last_update_timestamp_str,
last_update_timestamp,
enable_flag
from
wms_cre_credit_line_other


		and wms_cre_credit_line_other = #{wms_cre_credit_line_other}


		and business_model = #{business_model}


		and remark = #{remark}


		and wms_cre_credit_head_id = #{wms_cre_credit_head_id}


		and create_user_id = #{create_user_id}


		and create_user_name = #{create_user_name}


		and create_timestamp = #{create_timestamp}


		and last_update_user_id = #{last_update_user_id}


		and last_update_timestamp = #{last_update_timestamp}


		and enable_flag = #{enable_flag}
